sp_widget.client_script:
function() {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log("Note ID: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit("selectNote", id);
	}

	$rootScope.$on("updateNote", function(event, data) {
    c.data.notes.forEach(function(note) {
			if(note.sys_id == $rootScope.updatedNoteID) {
				note.title = data.title;
				note.note = data.note;
			}
		})
	});
}

sp_widget.script:
(function() {
	// Create an array to populate with notes
	data.notes = [];
	var noteGR = new GlideRecord("x_snc_createnotes_note");
	noteGR.addQuery("user", gs.getUser().getID());
	noteGR.orderByDesc("sys_created_on");
	noteGR.query();
	while(noteGR.next()) {
		var noteObj = {};
		// Use Service Portal helper method to get display values
		$sp.getRecordDisplayValues(noteObj, noteGR, "number,title,sys_id");
		// Get the first twenty characters of the description
		var noteString = noteGR.getValue("note");

		// Different from the example; add an ellipsis if the note
		// is longer than twenty characters
		if(noteString.length > 20) {
			noteObj.note = noteString.slice(0,20) + "...";
		} else {
			noteObj.note = noteString;
		}
		// Push the populated object onto the array
		data.notes.push(noteObj);
	}

})();
